    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        if(jTextField1.getText().length() != 11 && jTextField4.getText().length() != 10 && jTextField1.getText().isEmpty()
                && jTextField2.getText().isEmpty() && jTextField3.getText().isEmpty() && jTextField4.getText().isEmpty()
                && jTextField5.getText().isEmpty() && jTextField6.getText().isEmpty() && jTextField7.getText().isEmpty()){
            JOptionPane.showMessageDialog(this, "Lütfen bilgilerinizi geçerli giriniz!");
        }
        else if (!checkNumber(jTextField1.getText(), 11)){
            JOptionPane.showMessageDialog(this, "Kimlik numaranýz rakamlardan oluþmalýdýr!");
        }
        else if(jTextField5.getText().length() != 10 && !checkNumber(jTextField5.getText(), 10)){
            JOptionPane.showMessageDialog(this, "Telefon numaranýz baþýnda 0 olmadan 10 haneli olmalý ve rakamlardan oluþmalýdýr!");
        }
        else if(!checkDate()){
            JOptionPane.showMessageDialog(this, "Doðrum tarihiniz GG-AA-YYYY formatýnda olmalýdýr!");
        }
        else if(!checkString(jTextField2.getText(), 15) && !checkString(jTextField3.getText(), 15)){
            JOptionPane.showMessageDialog(this, "Adýnýz ve soyadýnýz sadece harflerden oluþup ve en fazla 15 harfli olmalýdýr!");
        }
        else if(jTextField6.getText().length() > 30){
            JOptionPane.showMessageDialog(this, "E-posta adresiniz en fazla 30 haneli olmalýdýr!");
        }
        else if(jTextField7.getText().length() > 16){
            JOptionPane.showMessageDialog(this, "Þifreniz en fazla 16  haneli olmalýdýr!");
        }
        else {
            Query db = new Query();
            Connection con = db.getCon();
            try {
                CallableStatement stmt = con.prepareCall("BEGIN\nkayit(?,?,?,?,?,?,?,?);\nEND;");
                stmt.setLong(1, Long.valueOf(jTextField1.getText()));
                stmt.setString(2, jTextField2.getText());
                stmt.setString(3, jTextField3.getText());
                stmt.setString(4, jTextField4.getText());
                stmt.setLong(5, Long.valueOf(jTextField5.getText()));
                stmt.setString(6, jTextField6.getText());
                stmt.setString(7, jTextField7.getText());
                stmt.registerOutParameter(8, java.sql.Types.INTEGER);
                stmt.execute();
                int basarili = stmt.getInt(8);
                if (basarili == 0){
                    JOptionPane.showMessageDialog(this, "Kaydýnýz baþarýyla gerçekleþmiþtir!");
                    setTextNull();
                }
                else if (basarili == 1){
                    JOptionPane.showMessageDialog(this, "Böyle bir kullanýcý zaten kayýtlýdýr!");
                }
            } catch (SQLException ex){
                System.out.println(ex);
            }
        }
    }//GEN-LAST:event_jButton1ActionPerformed

