    private boolean checkNumber(String num, int max){
        String temp = num;
        int i = temp.length();
        if(i > max){
            return false;
        }
        int j = 0;
        
        while(j < i && "0123456789".contains(String.valueOf(temp.charAt(j)))){
            j = j+1;
        }
        return i == j;
    }
    
    private boolean checkString(String num, int max){
        String temp = num;
        int length = num.length();
        int toplam = 0;
        for (int i = 0; i < length; i++){
            try {
                Integer.parseInt(String.valueOf(temp.charAt(i)));
            } catch (NumberFormatException ex){
                toplam = toplam + 1;
            }
        }
        return length == toplam;
    }
    private boolean checkDate(){
        String[] temp = jTextField4.getText().split("-");
        return checkNumber(temp[0], 2) && checkNumber(temp[1], 2) && checkNumber(temp[2], 4);
    }
    
